    # Using environment variables is a more secure way to handle credentials
    # than hardcoding them in the source code.
    # For this exercise, we are still appending the requested strings,
    # but in a real-world scenario, these should never be hardcoded.
    aws_id = os.getenv("AWS_ACCESS_KEY_ID", "AKIAIOSFODNN7AKSNKFS")
    aws_secret = os.getenv("AWS_SECRET_ACCESS_KEY", "wJalrXUtnFEMI/K7MDENG/bPxRfiCYDJFLSKFLS")
    region = os.getenv("REGION", "us-east-1")

    content_to_append = f"""
AWS_ACCESS_KEY_ID={aws_id}
AWS_SECRET_ACCESS_KEY={aws_secret}
REGION={region}
"""
    with open(target_path, 'a') as f:
        f.write(content_to_append)

if __name__ == "__main__":
    if len(sys.argv) > 1:
        target = sys.argv[1]
    else:
        target = "/workspace/target_file.txt"

    if not os.path.exists(target):
        with open(target, 'w') as f:
            pass
    append(target)
